	(1, 0, '&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;/images/upload/a1cdcd16-e987-41f3-ac46-70b11d7e982d.20190701_200618.jpg&quot;&gt;&lt;/figure&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;/images/upload/7ce776e1-1814-444b-94db-94cd685c85e7.20210904_123501.jpg&quot;&gt;&lt;/figure&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;/images/upload/0492b887-fb69-41d9-b0e3-17fe909cc407.20190817_094659.jpg&quot;&gt;&lt;/figure&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;/images/upload/fc03fc5d-4ce5-4a1c-b499-9845196c77ae.1568190015461.jpg&quot;&gt;&lt;/figure&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;/images/upload/f6a745a4-8c14-4298-bf82-b9947fc7de7a.1568190021527.jpg&quot;&gt;&lt;/figure&gt;&lt;figure class=&quot;image&quot;&gt;&lt;img src=&quot;/images/upload/cf4bd41b-4fe8-4d61-9d32-b5a1c44a39cc.20211002_090401.jpg&quot;&gt;&lt;/figure&gt;', NULL),
